mapper.select:
/* com.utour.mapper.ProductMapper.exists */

SELECT * FROM PRODUCT T
WHERE PRODUCT_ID = #{productId}

/* com.utour.mapper.ProductMapper.count */
SELECT COUNT(*) AS CNT
FROM PRODUCT T

    AND T.USE_YN = #{useYn}
    AND PRODUCT_ID = #{productId}
    AND PRODUCT_TYPE = #{productType}
    AND NATION_CODE = #{nationCode}
    AND AREA_CODE = #{areaCode}
    AND (TITLE LIKE CONCAT(#{query}, '%') OR CONTENT LIKE CONCAT(#{query}, '%'))

/* com.utour.mapper.ProductMapper.findPage */
SELECT
  T.PRODUCT_ID
, T.PRODUCT_TYPE
, T.NATION_CODE
, T.AREA_CODE
, T.REP_IMAGE_SRC
, T.USE_YN

, N.NATION_NAME
, NA.AREA_NAME
FROM PRODUCT T
LEFT OUTER JOIN NATION N ON T.NATION_CODE = N.NATION_CODE
LEFT OUTER JOIN NATION_AREA NA ON T.NATION_CODE = NA.NATION_CODE AND T.AREA_CODE = NA.AREA_CODE

    AND T.USE_YN = #{useYn}
    AND PRODUCT_ID = #{productId}
    AND PRODUCT_TYPE = #{productType}
    AND NATION_CODE = #{nationCode}
    AND AREA_CODE = #{areaCode}
    AND (TITLE LIKE CONCAT(#{query}, '%') OR CONTENT LIKE CONCAT(#{query}, '%'))

ORDER BY PRODUCT_ID DESC

mapper.insert:
/* com.utour.mapper.ProductMapper.save */
INSERT INTO PRODUCT (
  PRODUCT_ID
, PRODUCT_TYPE
, NATION_CODE
, AREA_CODE
, REP_IMAGE_SRC
, REP_IMAGE_PATH
, USE_YN

) VALUES (
  #{productId}
, #{productType}
, #{nationCode}
, #{areaCode}
, #{repImageSrc}
, #{repImagePath}
, #{useYn}

)

, REP_IMAGE_SRC = VALUES(REP_IMAGE_SRC)
, REP_IMAGE_PATH = VALUES(REP_IMAGE_PATH)
, NATION_CODE = VALUES(NATION_CODE)
, AREA_CODE = VALUES(AREA_CODE)
, CONTENT = VALUES(CONTENT)
, USE_YN = VALUES(USE_YN)

mapper.delete:
/* com.utour.mapper.ProductMapper.delete */
DELETE FROM PRODUCT
WHERE PRODUCT_ID = #{productId}
