mapper.select:
/* com.utour.mapper.ProductMapper.exists */

SELECT * FROM PRODUCT T
WHERE PRODUCT_ID = #{productId}

/* com.utour.mapper.ProductMapper.count */
SELECT COUNT(*) AS CNT
FROM PRODUCT T

    AND T.USE_YN = #{useYn}
    AND T.PRODUCT_ID = #{productId}
    AND T.PRODUCT_TYPE = #{productType}
    AND T.ARRIVAL_CODE = #{arrivalCode}
    AND T.AREA_CODE = #{areaCode}
    AND (T.TITLE LIKE CONCAT(#{query}, '%') OR T.DESCRIPTION LIKE CONCAT(#{query}, '%'))

/* com.utour.mapper.ProductMapper.findPage */
SELECT
  T.PRODUCT_ID
, T.PRODUCT_TYPE
, T.ARRIVAL_CODE
, T.AREA_CODE
, T.USE_YN
, T.DESCRIPTION

FROM PRODUCT T

    AND T.USE_YN = #{useYn}
    AND T.PRODUCT_ID = #{productId}
    AND T.PRODUCT_TYPE = #{productType}
    AND T.ARRIVAL_CODE = #{arrivalCode}
    AND T.AREA_CODE = #{areaCode}
    AND (T.TITLE LIKE CONCAT(#{query}, '%') OR T.DESCRIPTION LIKE CONCAT(#{query}, '%'))

ORDER BY PRODUCT_ID DESC

mapper.insert:
/* com.utour.mapper.ProductMapper.save */
INSERT INTO PRODUCT (
  PRODUCT_ID
, PRODUCT_TYPE
, ARRIVAL_CODE
, AREA_CODE
, REP_IMAGE_PATH
, USE_YN
, DESCRIPTION

) VALUES (
  #{productId}
, #{productType}
, #{arrivalCode}
, #{areaCode}
, #{repImagePath}
, #{useYn}
, #{description}

)

, REP_IMAGE_PATH = VALUES(REP_IMAGE_PATH)
, ARRIVAL_CODE = VALUES(ARRIVAL_CODE)
, AREA_CODE = VALUES(AREA_CODE)
, CONTENT = VALUES(CONTENT)
, USE_YN = VALUES(USE_YN)
, DESCRIPTION = VALUES(DESCRIPTION)

mapper.delete:
/* com.utour.mapper.ProductMapper.delete */
DELETE FROM PRODUCT
WHERE PRODUCT_ID = #{productId}
